acmoj1826.cpp<pre><code>#include &lt;iostream&gt;
#define N 1000
using namespace std;
struct tree {
int left;
int right;
int v;
} ts[N];
int sum() {
int x = 0, s = 0;
while( x != -1 ){
if ( ts[x].left == -1 ){
s += ts[x].v; // 没有左子树，直接走右结点
x = ts[x].right;
} else {
int y = ts[x].left;
while( ts[y].right != -1 &amp;&amp; ts[y].right != x ) // 找到左子树的最右结点
y = ts[y].right;
if ( ts[y].right == -1 ){ // 左子树还没有遍历过
s += ts[x].v;
ts[y].right = x;
x = ts[x].left; } else { // 左子树已经遍历完了
ts[y].right = -1;
x = ts[x].right;
}
}
return s;
}
int main() {
int n;
cin &gt;&gt; n;
for (int i = 0; i &lt; n; i++) {
cin &gt;&gt; ts[i].left;
cin &gt;&gt; ts[i].right;
cin &gt;&gt; ts[i].v;
}
cout &lt;&lt; sum();
}
</code></pre>
